var fs = require("fs");

exports.open = function(project, gui){
    var str = JSON.stringify(project);
    fs.writeFile("temp/presentation.json", str, function(err){
        if(err) console.error(err);
        else{
            gui.Window.open("resources/presentation.html", {
                fullscreen: false,
                toolbar: false,
                show: false
            });
        }
    });
}
